import React, { useState } from 'react';
//import { Link} from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const RegisterForm = () => {
    const [formData, setFormData] = useState({
      name: '',
      email: '',
      password: ''
    });

    const handleChange = (e) => {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    };
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      alert("Passwords don't match.");
      return;
    }
    try {
        const response = await axios.post('http://localhost:3000/register', formData);
      console.log('Registration successful:', response.data);
      // Optionally, redirect to login or dashboard after successful registration
    } catch (error) {
      console.error('Registration error:', error);
    }
  };

  return (
    <body>
        <section>
    <form onSubmit={handleSubmit}>
      <h1>Register</h1>
      <div class="inputbox">
      <input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Email"
        required
      />
      </div>
      <div class="inputbox">
      <input
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Password"
        required
      />
      </div>
      <div class="inputbox">
      <input
        type="password"
        value={confirmPassword}
        onChange={(e) => setConfirmPassword(e.target.value)}
        placeholder="Confirm Password"
        requireds
      />
      </div>
      <button type="submit" navigate='/login'>Register</button>
      <div className="register">
            <p>Don't have an account? 
              <button type="button" onClick={() => navigate('/login')} style={{ background: 'none', border: 'none', color: 'white', textDecoration: 'underline', cursor: 'pointer' }}>
                Login
              </button>
            </p>
          </div>
    </form>
    </section>
    </body>
  );
};

export default RegisterForm;
